(167, 1, '2015-02-11 08:27:17', '2015-02-11 08:27:17', '<span class="tooltips">CSS Tooltips<span>I am a verywide Tooltip with bigger amount<br />of data!!!<br />I am about to be bigger and bigger</span></span>\n\n<p id="slide_12_top">[simple_tooltip content=''This is the content for the tooltip bubble'']Human Factors is the interaction between:[/simple_tooltip]</p>\n<img class="aligncenter size-full wp-image-166" src="http://localhost/aviation-safety/wp-content/uploads/2014/11/slide_12.png" alt="slide_12" width="1843" height="1382" />', 'Slide 12 - SHEL MODEL', '', 'inherit', 'closed', 'open', '', '80-autosave-v1', '', '', '2015-02-11 08:27:17', '2015-02-11 08:27:17', '', 80, 'http://localhost/aviation-safety/80-autosave-v1/', 0, 'revision', '', 0),
(168, 1, '2014-11-14 07:57:23', '2014-11-14 07:57:23', '<ul class="big_ul">\n<li>S â Software â Manuals, Procedures, Regulations, Standards</li>\n<li>H â Hardware â Equipment</li>\n<li>E â Environment</li>\n<li>L â Liveware â The Human</li>\n</ul>\n\n\0', 'Slide 13 - SHEL MODEL Question', '', 'inherit', 'closed', 'open', '', '81-autosave-v1', '', '', '2014-11-14 07:57:23', '2014-11-14 07:57:23', '', 81, 'http://localhost/aviation-safety/81-autosave-v1/', 0, 'revision', '', 0),
